package tw.north27.coachingapp.media

import androidx.lifecycle.MutableLiveData
import tw.north27.coachingapp.base.BaseViewModel
import tw.north27.coachingapp.ext2.asLiveData

class MediaPhotoViewModel : BaseViewModel() {

    private val _url = MutableLiveData<String>(
        "data:image/jpeg;base64,"
    )

    val url = _url.asLiveData()

    fun setUrl(url: String) {
        _url.value = url
    }

    private val _byteArray = MutableLiveData<ByteArray>()

    val byteArray = _byteArray.asLiveData()

    fun setByteArray(byteArray: ByteArray) {
        _byteArray.value = byteArray
    }
}